{{ Html::script('vendor/jquery-simplecolorpicker/jquery.simplecolorpicker.js') }}
{{ Html::style('vendor/jquery-simplecolorpicker/jquery.simplecolorpicker.css') }}
{{ Html::style('vendor/jquery-simplecolorpicker/jquery.simplecolorpicker-fontawesome.css') }}
<script>
    $('.select2').select2(Init.select2());

    $(document).ready(function () {
        $('select[name="color"]').simplecolorpicker({theme: 'fontawesome'});
        $('#modal-remote .select2').select2(Init.select2());
    })

    $('[name=color],[name=rack],[name=bay],[name=level],[name=position]').on('change', function(){
        var rack     = $('[name=rack]').val();
        var bay      = $('[name=bay]').val();
        var level    = $('[name=level]').val();
        var position = $('[name=position]').val();
        var color    = $('[name=color]').val();
        var code     = rack + '-' + bay + '-' + level + position

        $('.code-preview span').html(code);
        $('.code-preview i').css('color', color);
        $('[name=code]').val(code);
    })

    $('[name=color],[name=rack],[name=bay],[name=level],[name=position]').on('focus', function(){
        $('.preview-type').hide();
        $('.default-img').show();
    })

    $(document).on('mouseover', '#select2-type_id-results .select2-results__option', function(e){
        var index = $(this).index();
        var img = $('[name="type_id"] option').eq(index).data('image');
        $('.default-img').hide();
        $('.preview-type').attr('src', img).show().css('display', 'inline-block');

       console.log(img)
    })

    /**
     * Submit form
     *
     * @param {type} param1
     * @param {type} param2S
     */
    $('.form-rack').on('submit', function(e){
        e.preventDefault();

        var $form = $(this);
        var $button = $('button[type=submit]');

        $button.button('loading');
        $.post($form.attr('action'), $form.serialize(), function(data){
            if(data.result) {
                oTable.draw(); //update datatable
                $.bootstrapGrowl(data.feedback, {type: 'success', align: 'center', width: 'auto', delay: 8000});
                $('#modal-remote').modal('hide');
            } else {
                $('.form-billing .modal-feedback').html('<i class="fas fa-exclamation-circle"></i> ' + data.feedback);
            }

        }).error(function () {
            $('.form-billing .modal-feedback').html('<i class="fas fa-exclamation-circle"></i> Erro de processamento interno. Não foi possível submeter e concluir o seu pedido.');
        }).always(function(){
            $button.button('reset');
        })
    });
</script>

